mapper.select:
select

from post_answer


        and pa_id=#{paId,jdbcType=INTEGER}


        and fk_p_id=#{fkPId,jdbcType=INTEGER}


        and pas_id=#{pasId,jdbcType=INTEGER}


        and fk_u_id=#{fkUId,jdbcType=INTEGER}


        and pa_content=#{paContent,jdbcType=VARCHAR}


        and pa_click_a=#{paClickA,jdbcType=INTEGER}


        and pa_date=#{paDate,jdbcType=TIMESTAMP}


select

from post_answer
where pa_id = #{fkPIdB,jdbcType=INTEGER}
and pas_id = null;

select count(1)
from post_answer
where fk_p_id=#{fkPId,jdbcType=INTEGER}
